sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ( newValue === '') {
      return;
   }

	 //Type appropriate comment here, and begin script below

	//storing all the choice of the what needed field
	 var whatneeded=g_form.getValue('u_what_needed');

	//Clear all the choices from what needed field in what needed
	g_form.clearOptions('u_what_needed');

	//seting value of short description based on  requested type
	if(newValue=='hr'){
		g_form.setValue('short_description','Request type=Human resources');
		g_form.addOption('u_what_needed','hr1','Human Resource 1');
		g_form.addOption('u_what_needed','hr2','Human Resource 2');
		g_form.addOption('u_what_needed','other', 'Other');
	}
	else if(newValue=='facilities'){
		g_form.setValue('short_description','Request type=Facilities');
		g_form.addOption('u_what_needed','facilities 1', 'Facilities 1');
		g_form.addOption('u_what_needed','facilities 2', 'Facilities 2');
		g_form.addOption('u_what_needed','other', 'Other');
	}
	else if(newValue=='your_choice'){
		g_form.setValue('short_description','Request type=Your department Choice');
		g_form.addOption('u_what_needed','your_choice 1','Your Department Choice 1');
		g_form.addOption('u_what_needed','your_choice 2','Your Department Choice 2');
		g_form.addOption('u_what_needed','other', 'Other');
	}
	else{
		g_form.setValue('short_description','Request type=Legal');
		g_form.addOption('u_what_needed','legal 1','Legal 1');
		g_form.addOption('u_what_needed','legal 2','Legal 2');
		g_form.addOption('u_what_needed','other', 'Other');
	} 

	//if form is loading and it is not the first record set the u_what_needed value to
	//the value to the record is was loaded before.

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed',whatneeded);
	}


}
